import React from 'react'
import "./App.css"
import SignIn from './SignIn';
import { useState } from 'react';
import HomeBody from './HomeBody';


function NavBar() {
    const [selectedOption, setSelectedOption] = useState('Accueil');
    const handleOptionChange = (option) => {
        setSelectedOption(option);
    };

    return (
        <div class="menu">
        <img src="images/TUNISIA-Logo.png" class="logo" alt=""/>

        <ul className='nav-links'>
            <li onClick={() => handleOptionChange('Accueil')}>Home</li>
            <li onClick={() => handleOptionChange('Services')}>Services</li>
            <li onClick={() => handleOptionChange('À Propos')}>About Us</li>
            <li onClick={() => handleOptionChange('Contact')}>Contact us</li>
        </ul>
        <div class="nav-language">
            <img src="images/us_flag.png" width="25" alt="" />
            <p>EN</p>
            <img src="images/dropdown_icon.png" width="8" alt="" />
            <img className='search' src="images/search_icon.png" alt=""/>
            <button type="button" onClick={SignIn()} className='signInButton'>Sign In</button>
        </div>
        <div>
            {selectedOption === 'Accueil' && <Accueil />}
            {selectedOption === 'Services' && <Services />}
            {selectedOption === 'À Propos' && <APropos />}
            {selectedOption === 'Contact' && <Contact />}
        </div>
    </div>
    );
}

function Accueil() {
    return <HomeBody></HomeBody>;
}

function Services() {
return <h2>Nos Services</h2>;
}

function APropos() {
return <h2>À Propos de Nous</h2>;
}

function Contact() {
return <h2>Contactez-nous</h2>;
}

export default NavBar;
