import './App.css';
import React from 'react';
import NavBar from './NavBar';
import HomeBody from './HomeBody';
import { useState } from 'react';

//import SignIn from './SignIn';

/*function App() {
  return (
    <>
      <NavBar/>
      <HomeBody/>
    </>
  );
}*/

function App() {
  const [selectedOption, setSelectedOption] = useState('Accueil');

  const handleOptionChange = (option) => {
    setSelectedOption(option);
  };

  return (
    <div>
      <nav>
        <ul>
          <li onClick={() => handleOptionChange('Accueil')}>Accueil</li>
          <li onClick={() => handleOptionChange('Services')}>Services</li>
          <li onClick={() => handleOptionChange('À Propos')}>À Propos</li>
          <li onClick={() => handleOptionChange('Contact')}>Contact</li>
        </ul>
      </nav>

      <div>
        {selectedOption === 'Accueil' && <Accueil />}
        {selectedOption === 'Services' && <Services />}
        {selectedOption === 'À Propos' && <APropos />}
        {selectedOption === 'Contact' && <Contact />}
      </div>
    </div>
  );
}


export default App;
