import React from 'react'
import "./App.css"
import SignIn from './SignIn';
import { useState } from 'react';
import HomeBody from './HomeBody';
import { useEffect } from 'react';

function NavBar() {
    return (
        <div className="menu">
            <img src="public/TUNISIA-Logo.png" className="logo" alt="" />

            <ul>
                <li>Places to go</li>
                <li>Magazine</li>
                <li>Ticket</li>
                <li>Contact us</li>
            </ul>
            <div className="nav-language">
                <img src="images/us_flag.png" width="25" alt="" />
                <p>EN</p>
                <img src="images/dropdown_icon.png" width="8" alt="" />
                <img src="images/search_icon.png" alt="" className="search" />
            </div>
        </div>
    );
}

export default NavBar ;

