import React from 'react'
import "./App.css"
/*import SignIn from './SignIn';*/
import { useState } from 'react';
/*import HomeBody from './HomeBody';
import { useEffect } from 'react';*/

function NavBar() {
    const [selectedOption, setSelectedOption] = useState('Accueil');
    const handleOptionChange = (option) => {
        setSelectedOption(option);
    };

    return (
        <div className="menu">
            <img src="public\TUNISIA-Logo.png" className="logo" alt="" />

            <ul>
                <li onClick={() => handleOptionChange('Accueil')}>Home</li>
                <li onClick={() => handleOptionChange('Services')}>Services</li>
                <li onClick={() => handleOptionChange('À Propos')}>About Us</li>
                <li onClick={() => handleOptionChange('Contact')}>Contact us</li>
            </ul>
            <div className="nav-language">
                <img src="public\us_flag.png" width="25" alt="" />
                <p>EN</p>
                <img src="public\dropdown_icon.png" width="8" alt="" />
                <img src="public\search_icon.png" alt="" className="search" />
                <button type="button" onClick={() => handleOptionChange('Sign In')} className='signInButton'>Sign In</button> 

            </div>
        </div>
    );
}

export default NavBar ;

